const baseUrl = "http://127.0.0.1:8000/"

export const getNews = () => {
    fetch(baseUrl + 'api/get_all_news/')
    .then(res => res.json())
}

export const getStadiums = () => {
    fetch(baseUrl + 'api/get_all_stadiums/')
    .then(res => res.json())
}

export const getCommands = () => 
    fetch(baseUrl + 'api/get_all_commands/')
    .then(res => res.json())
