import "./style.css";
import {BrowserRouter as Router, Route, Routes} from "react-router-dom"
import Header from "./Header";
import MainPage from "./MainPage";
import TurnierTable from "./TurnierTable";
import Stadiums from "./Stadiums";
import Commands from "./Commands";
import Predictions from "./Predictions";
import Login from "./Login";
import {useState, useEffect} from 'react';

const startCommand = {id: 1, country: "Австралия", image: "/australia_team.jpg", info: "Информация о команде"}

function App() {
  let [command, setCommand] = useState(startCommand)

  useEffect(() => {
      const row = localStorage.getItem('command')
      setCommand(JSON.parse(row))
  }, [])

  useEffect(() => {
      localStorage.getItem('command', JSON.stringify(command))
  }, [command])


  return (
    <div className="App">
      <Header className="Header"/>

      <Router>
      <Routes>
        <Route path='/' element={<MainPage/>}/>
        <Route path='/tournier-table' element={<TurnierTable/>}/>
        <Route path='/stadiums' element={<Stadiums/>}/>
        <Route path='/commands' element={<Commands command={command}/>}/>
        <Route path='/predictions' element={<Predictions/>}/>
        <Route path='/login' element={<Login/>}/>
      </Routes>
      </Router>
    </div>
  );
}

export default App;
